import React from 'react'

import { useSelector, useDispatch } from 'react-redux'

const App = () => {

  const anecdotes = useSelector(state => { state.content})

  const dispatch = useDispatch()

  const vote = (event, id) => {

    event.preventDefault()

    console.log('2. vote', id)

    dispatch({
      type: 'VOTE',
      payload: id
    })
  }

  console.log('2. anecdotes: ', typeof anecdotes, anecdotes)

  return (
    <div>
      <h2>Anecdotes</h2>
      {
        Object.keys(anecdotes).map((anecdote, index) =>

        <div key={anecdote.id}>
          <div>
            {anecdotes[index].content}
          </div>
          <div>
            has {anecdotes[index].votes}
            <button onClick={(event) => vote(anecdotes[index].id)}>vote</button>
          </div>
        </div>
      )}
      <h2>create new</h2>
      <form>
        <div><input /></div>
        <button>create</button>
      </form>
    </div>
  )
}

export default App
